require 'rails_helper'\n\nRSpec.describe Company, type: :model do\n  describe 'validações' do\n    it 'valida URL do WhatsApp se ativado' do\n      company = Company.new(whatsapp_enabled: true, whatsapp_url: 'invalid')\n      expect(company).not_to be_valid\n      company.whatsapp_url = 'https://wa.me/123'\n      expect(company).to be_valid\n    end\n    it 'persiste configurações' do\n      company = create(:company, whatsapp_enabled: true, whatsapp_url: 'https://wa.me/123')\n      expect(company.reload.whatsapp_url).to eq('https://wa.me/123')\n    end\n  end\nend